namespace Mageplaza\ShippingRestriction\Model\Config\Source;

use Magento\Framework\Option\ArrayInterface;

/**
 * Class Location
 * @package Mageplaza\ShippingRestriction\Model\Config\Source
 */
class Location implements ArrayInterface
{
    const ORDER_BACKEND = 1;
    const ORDER_FRONTEND = 2;

    /**
     * @return array
     */
    public function toOptionArray()
    {
        $options = [];
        foreach ($this->toArray() as $value => $label) {
            $options[] = [
                'value' => $value,
                'label' => $label
            ];
        }

        return $options;
    }

    /**
     * Get options in "key-value" format
     *
     * @return array
     */
    public function toArray()
    {
        return [
            self::ORDER_BACKEND => __('Backend Orders'),
            self::ORDER_FRONTEND => __('Storefront Purchases')
        ];
    }
}
